changeSet.sql:
delete from settings where name='interval' and parentid=(select id from settings where parentid=1 and name='indexoptimizer')
insert into settings (id, parentid, name, value) select max(id)+1,(select id from settings where parentid=1 and name='indexoptimizer'), 'enable', 'true' from settings; 
insert into settings (id, parentid, name, value) select max(id)+1,(select id from settings where parentid=1 and name='indexoptimizer'), 'at', null from settings; 
insert into settings (id, parentid, name, value) select max(id)+1,max(id), 'hour', '0' from settings; 
insert into settings (id, parentid, name, value) select max(id)+1,max(id)-1, 'min', '0' from settings; 
insert into settings (id, parentid, name, value) select max(id)+1,max(id)-2, 'sec', '0' from settings; 
insert into settings (id, parentid, name, value) select max(id)+1,(select id from settings where parentid=1 and name='indexoptimizer'), 'interval', null from settings; 
insert into settings (id, parentid, name, value) select max(id)+1,max(id), 'day', '0' from settings; 
insert into settings (id, parentid, name, value) select max(id)+1,max(id)-1, 'hour', '0' from settings; 
insert into settings (id, parentid, name, value) select max(id)+1,max(id)-2, 'min', '0' from settings;
